mapper.update:
update t_liuyan
set user_name = #{userName,jdbcType=VARCHAR},
  liuyan = #{liuyan,jdbcType=VARCHAR},
  release_time = #{releaseTime,jdbcType=TIMESTAMP}
where ser_num = #{serNum,jdbcType=BIGINT}

mapper.select:
select ser_num, user_name, liuyan, release_time
from t_liuyan
where ser_num = #{serNum,jdbcType=BIGINT}
