mapper.update:
update t_notice
set admin_id = #{adminId,jdbcType=INTEGER},
  notice = #{notice,jdbcType=VARCHAR},
  release_time = #{releaseTime,jdbcType=TIMESTAMP}
where ser_num = #{serNum,jdbcType=INTEGER}

mapper.select:
select ser_num, admin_id, notice, release_time
from t_notice
where ser_num = #{serNum,jdbcType=INTEGER}
